'DECLARE',
'',
'   V_CLOB          CLOB;',
'   V_CLOB_LENGTH   NUMBER;',
'   V_POS_DEB       NUMBER;',
'   V_POS_FIN       NUMBER;',
'   V_RETOUR        VARCHAR2(30000 CHAR) := NULL;',
'',
'BEGIN',
'',
'   SELECT JOURNAL',
'         ,DBMS_LOB.GETLENGTH(JOURNAL)',
'     INTO V_CLOB',
'         ,V_CLOB_LENGTH',
'     FROM SDBM.HIST_TACHE_AGT',
'    WHERE ID_SOUMISSION = :P221_ID_SOUMISSION;',
'',
'   IF (V_CLOB_LENGTH <= 30000) THEN',
'      V_POS_DEB := 1;',
'   ELSE',
'      V_POS_DEB := V_CLOB_LENGTH - 30000 + 1;',
'   END IF;',
'   V_POS_FIN := V_CLOB_LENGTH;',
'   ',
'   WHILE (V_POS_DEB <= V_POS_FIN) LOOP',
'      V_RETOUR  := V_RETOUR || DBMS_LOB.SUBSTR(V_CLOB,4000,V_POS_DEB);',
'      V_POS_DEB := V_POS_DEB + 4000;',
'   END LOOP;',
'',
'   -- Retour',
'   RETURN V_RETOUR;',
'',
'END;'))
