,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'',
unistr('   -- Liste des \00E9l\00E9ments associ\00E9s dans la base de donn\00E9es'),
'   CURSOR C_REPARATION_ASSOC IS',
'      SELECT NOM_REPARATION',
'        FROM #OWNER#.REPARATION_EVEN_CIBLE',
'       WHERE TYPE_CIBLE      = ''BD''',
'         AND NOM_CIBLE       = :P3142_NOM_CIBLE',
'         AND NOM_EVENEMENT   = :P3142_NOM_EVENEMENT',
'       ORDER BY NOM_REPARATION;',
'',
'   V_TABLEAU_BD  HTMLDB_APPLICATION_GLOBAL.VC_ARR2;',
'   V_TABLEAU_EC  HTMLDB_APPLICATION_GLOBAL.VC_ARR2;',
'   V_INDICE      NUMBER;',
'   V_EXISTE      BOOLEAN;',
'',
'BEGIN',
'',
'   --',
'   -- Formulaire pricipal',
'   --',
'',
'   UPDATE #OWNER#.EVENEMENT_CIBLE',
'      SET VERIFICATION       = :P3142_VERIFICATION',
'         ,DH_PROCHAINE_VERIF = :P3142_DH_PROCHAINE_VERIF',
'         ,INTERVAL           = :P3142_INTERVAL',
'         ,DESTI_NOTIF        = DECODE(:P3142_DESTI_NOTIF',
'                                     ,''!!NULL!!'',TO_CHAR(NULL)',
'                                     ,:P3142_DESTI_NOTIF',
'                                     )',
'    WHERE ROWID = :P3142_ROWID;',
'',
'   --',
'   -- Gestion des associations',
'   --',
'',
unistr('   -- Obtenir la liste des \00E9l\00E9ments actuellement associ\00E9s BD'),
'   V_INDICE := 1;',
'   FOR RC_REPARATION_ASSOC IN C_REPARATION_ASSOC LOOP',
'      V_TABLEAU_BD(V_INDICE) := RC_REPARATION_ASSOC.NOM_REPARATION;',
'      V_INDICE := V_INDICE + 1;',
'   END LOOP;',
'',
unistr('   -- Obtenir la liste des \00E9l\00E9ments actuellement associ\00E9s \00C9CRAN'),
'   V_TABLEAU_EC := APEX_UTIL.STRING_TO_TABLE(:P3142_ASSOCIATION_REPARATION);',
'',
'   --',
unistr('   -- Parcours de la liste pour g\00E9rer les retraits'),
'   --',
'',
unistr('   -- Pour chaque \00E9l\00E9ment de la liste BD'),
'   FOR I IN 1..V_TABLEAU_BD.COUNT LOOP',
'',
'      -- Recherche dans la liste active (si n''existe pas, retrait)',
'      V_EXISTE := FALSE;',
'      FOR J IN 1..V_TABLEAU_EC.COUNT LOOP',
'',
'         -- Test de l''existance',
'         IF (V_TABLEAU_BD(I) = V_TABLEAU_EC(J)) THEN',
'            V_EXISTE := TRUE;',
'            EXIT;',
'         END IF;',
'',
'      END LOOP;',
'',
unistr('      -- Si l''\00E9l\00E9ment n''a pas \00E9t\00E9 localis\00E9'),
'      IF (V_EXISTE = FALSE) THEN',
'',
'         DELETE FROM #OWNER#.REPARATION_EVEN_CIBLE',
'          WHERE TYPE_CIBLE      = ''BD''',
'            AND NOM_CIBLE       = :P3142_NOM_CIBLE',
'            AND NOM_EVENEMENT   = :P3142_NOM_EVENEMENT',
'            AND NOM_REPARATION  = V_TABLEAU_BD(I);',
'',
'      END IF;',
'',
'   END LOOP;',
'',
'   --',
unistr('   -- Parcours de la liste pour g\00E9rer les ajouts'),
'   --',
'',
unistr('   -- Pour chaque \00E9l\00E9ment de la liste active'),
'   FOR I IN 1..V_TABLEAU_EC.COUNT LOOP',
'',
'      -- Recherche dans la liste BD (si n''existe pas, ajout)',
'      V_EXISTE := FALSE;',
'      FOR J IN 1..V_TABLEAU_BD.COUNT LOOP',
'',
'         -- Test de l''existance',
'         IF (V_TABLEAU_EC(I) = V_TABLEAU_BD(J)) THEN',
'            V_EXISTE := TRUE;',
'            EXIT;',
'         END IF;',
'',
'      END LOOP;',
'',
unistr('      -- Si l''\00E9l\00E9ment n''a pas \00E9t\00E9 localis\00E9'),
'      IF (V_EXISTE = FALSE) THEN',
'',
'         INSERT INTO #OWNER#.REPARATION_EVEN_CIBLE',
'         (',
'            TYPE_CIBLE                     ',
'           ,SOUS_TYPE_CIBLE                ',
'           ,NOM_CIBLE                      ',
'           ,NOM_EVENEMENT                  ',
'           ,NOM_REPARATION                  ',
'         )',
'         VALUES',
'         (',
'            ''BD''',
'            ,(SELECT SOUS_TYPE_CIBLE',
'                FROM #OWNER#.CIBLE',
'               WHERE TYPE_CIBLE = ''BD''',
'                 AND NOM_CIBLE  = :P3142_NOM_CIBLE',
'             )',
'            ,:P3142_NOM_CIBLE',
'            ,:P3142_NOM_EVENEMENT',
'            ,V_TABLEAU_EC(I)',
'         );',
'',
'      END IF;',
'',
'   END LOOP;',
'',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_process_error_message=>unistr('Probl\00E8me pendant la modification de CIBLE.')
,p_process_when_button_id=>wwv_flow_api.id(3399618406492245)
,p_process_success_message=>unistr('Action trait\00E9e.')
);
