,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'',
'   -- SQL - Serveur - Variable',
'   V_SQL_CHART VARCHAR2(32767 CHAR);',
'',
'   -- SQL - Cible',
'   C_REG_SQL_CIBLE CONSTANT VARCHAR2(2000 CHAR) :=',
'       ''SELECT TRUNC(DH_COLLECTE_DONNEE)                    "Date"''                                                       || CHR(10) ||',
'       ''      ,{O}(CPU_USED_BY_SESSION_C_DERN_PER * 100 +''                                                                || CHR(10) ||',
'       ''           CPU_RECURSIVE_C_DERN_PER       * 100 +''                                                                || CHR(10) ||',
'       ''           CPU_PARSE_TIME_C_DERN_PER      * 100''                                                                  || CHR(10) ||',
'       ''          )                                         "Total"''                                                      || CHR(10) ||',
'       ''      ,{O}(CPU_USED_BY_SESSION_C_DERN_PER * 100)    "By session"''                                                 || CHR(10) ||',
'       ''      ,{O}(CPU_RECURSIVE_C_DERN_PER       * 100)    "Recursive"''                                                  || CHR(10) ||',
'       ''      ,{O}(CPU_PARSE_TIME_C_DERN_PER      * 100)    "Parse time"''                                                 || CHR(10) ||',
'       ''  FROM #OWNER#.CD_SYSSTAT_CPU''                                                                                    || CHR(10) ||',
'       '' WHERE DH_COLLECTE_DONNEE             > SYSDATE - TO_NUMBER(SUBSTR(:P21452_NB_JOUR,1,LENGTH(:P21452_NB_JOUR)-1))'' || CHR(10) ||',
'       ''   AND NOM_CIBLE                      = :P21452_NOM_CIBLE''                                                        || CHR(10) ||',
'       ''   AND CPU_USED_BY_SESSION_C_DERN_PER IS NOT NULL''                                                                || CHR(10) ||',
'       ''   AND CPU_RECURSIVE_C_DERN_PER       IS NOT NULL''                                                                || CHR(10) ||',
'       ''   AND CPU_PARSE_TIME_C_DERN_PER      IS NOT NULL''                                                                || CHR(10) ||',
'       '' GROUP BY TRUNC(DH_COLLECTE_DONNEE)''                                                                              || CHR(10) ||',
'       '' ORDER BY 1, 2''                                                                                                   || CHR(10) ;',
' ',
unistr('   -- SQL - Cible - Base de donn\00E9es RAC (total des instances)'),
'   C_RAC_SQL_CIBLE CONSTANT VARCHAR2(2000 CHAR) :=',
'       ''SELECT TRUNC(DH_COLLECTE_DONNEE)                    "Date"''                                                             || CHR(10) ||',
'       ''      ,{O}(CPU_USED_BY_SESSION_C_DERN_PER * 100 +''                                                                      || CHR(10) ||',
'       ''           CPU_RECURSIVE_C_DERN_PER       * 100 +''                                                                      || CHR(10) ||',
'       ''           CPU_PARSE_TIME_C_DERN_PER      * 100''                                                                        || CHR(10) ||',
'       ''          )                                         "Total"''                                                            || CHR(10) ||',
'       ''      ,{O}(CPU_USED_BY_SESSION_C_DERN_PER * 100)    "By session"''                                                       || CHR(10) ||',
'       ''      ,{O}(CPU_RECURSIVE_C_DERN_PER       * 100)    "Recursive"''                                                        || CHR(10) ||',
'       ''      ,{O}(CPU_PARSE_TIME_C_DERN_PER      * 100)    "Parse time"''                                                       || CHR(10) ||',
'       ''  FROM #OWNER#.CD_SYSSTAT_CPU''                                                                                          || CHR(10) ||',
'       '' WHERE DH_COLLECTE_DONNEE             > SYSDATE - TO_NUMBER(SUBSTR(:P21452_NB_JOUR,1,LENGTH(:P21452_NB_JOUR)-1))''       || CHR(10) ||',
'       ''   AND NOM_CIBLE                      IN (SELECT NOM_CIBLE FROM #OWNER#.CIBLE WHERE NOM_CIBLE_REF = :P21452_NOM_CIBLE)'' || CHR(10) ||',
'       ''   AND CPU_USED_BY_SESSION_C_DERN_PER IS NOT NULL''                                                                      || CHR(10) ||',
'       ''   AND CPU_RECURSIVE_C_DERN_PER       IS NOT NULL''                                                                      || CHR(10) ||',
'       ''   AND CPU_PARSE_TIME_C_DERN_PER      IS NOT NULL''                                                                      || CHR(10) ||',
'       '' GROUP BY TRUNC(DH_COLLECTE_DONNEE)''                                                                                    || CHR(10) ||',
'       '' ORDER BY 1, 2''                                                                                                         || CHR(10) ;',
'',
'   V_IND NUMBER(1);',
'   V_OPE VARCHAR2(10 CHAR);',
'',
'BEGIN',
'',
unistr('   -- Moyenne / Maximum (dernier carat\00E8re du SELECT LIST du nombre de jour)'),
'   IF ((:P21452_NB_JOUR IS NULL) OR (SUBSTR(:P21452_NB_JOUR,-1,1) = ''A'')) THEN',
'      V_OPE := ''AVG'';',
'   ELSE',
'      V_OPE := ''MAX'';',
'   END IF;',
'   ',
'   BEGIN',
'      ',
unistr('      -- V\00E9rification si c''est une base de donn\00E9es RAC'),
'      SELECT 1',
'        INTO V_IND',
'        FROM #OWNER#.CIBLE',
'       WHERE NOM_CIBLE = :P21452_NOM_CIBLE',
'         AND TYPE_BD = ''RD'';',
'',
'      -- Niveau cible',
'      RETURN(REPLACE(C_RAC_SQL_CIBLE,''{O}'',V_OPE));',
'',
'   EXCEPTION',
'',
'      WHEN NO_DATA_FOUND THEN',
'         RETURN(REPLACE(C_REG_SQL_CIBLE,''{O}'',V_OPE));',
'',
'   END;',
'',
'END;'))
,p_plug_source_type=>'NATIVE_JET_CHART'
,p_plug_query_options=>'DERIVED_REPORT_COLUMNS'
