'DECLARE',
'',
'   -- SQL - Serveur',
'   C_REG_SQL_SERVEUR CONSTANT VARCHAR2(2000 CHAR) :=',
'       ''SELECT TRUNC("Date")                        "Date"''                                                                                               || CHR(10) ||',
'       ''      ,"Instance"                           "Instance"''                                                                                           || CHR(10) ||',
'       ''      ,{O}("Total") * 100                   "Total"''                                                                                              || CHR(10) ||',
'       ''  FROM (''                                                                                                                                         || CHR(10) ||',
'       ''        SELECT TRUNC(DH_COLLECTE_DONNEE, ''''MI'''') - MOD(EXTRACT(MINUTE FROM CAST(DH_COLLECTE_DONNEE AS TIMESTAMP)), 10) / 1440  "Date"''            || CHR(10) ||',
'       ''              ,INSTANCE_NAME || ''''@'''' || HOST_NAME                                                                             "Instance"''        || CHR(10) ||',
'       ''              ,AVG(NVL(CPU_USED_BY_SESSION_C_DERN_PER,0) + NVL(CPU_RECURSIVE_C_DERN_PER,0) + NVL(CPU_PARSE_TIME_C_DERN_PER,0)) "Total"''           || CHR(10) ||',
'       ''          FROM #OWNER#.CD_SYSSTAT_CPU''                                                                                                            || CHR(10) ||',
'       ''         WHERE DH_COLLECTE_DONNEE > SYSDATE - TO_NUMBER(SUBSTR(:P21452_NB_JOUR,1,LENGTH(:P21452_NB_JOUR)-1))''                                     || CHR(10) ||',
'       ''           /* Obtenir les statistiques du serveur courrant */''                                                                                    || CHR(10) ||',
'       ''           AND HOST_NAME = (SELECT HOST_NAME''                                                                                                     || CHR(10) ||',
'       ''                              FROM #OWNER#.CD_SYSSTAT_CPU''                                                                                        || CHR(10) ||',
'       ''                             WHERE DH_COLLECTE_DONNEE = (SELECT MAX(DH_COLLECTE_DONNEE)''                                                          || CHR(10) ||',
'       ''                                                           FROM #OWNER#.CD_SYSSTAT_CPU''                                                           || CHR(10) ||',
'       ''                                                          WHERE NOM_CIBLE = :P21452_NOM_CIBLE''                                                    || CHR(10) ||',
'       ''                                                        )''                                                                                        || CHR(10) ||',
'       ''                               AND NOM_CIBLE          = :P21452_NOM_CIBLE''                                                                        || CHR(10) ||',
'       ''                           )''                                                                                                                     || CHR(10) ||',
'       ''         GROUP BY TRUNC(DH_COLLECTE_DONNEE, ''''MI'''') - MOD(EXTRACT(MINUTE FROM CAST(DH_COLLECTE_DONNEE AS TIMESTAMP)), 10) / 1440''                 || CHR(10) ||',
