'   ELSE',
'      V_OPE := ''MAX'';',
'   END IF;',
'   ',
'   -- Niveau serveur',
'   RETURN(REPLACE(C_REG_SQL_SERVEUR,''{O}'',V_OPE));',
'',
'END;'))
