,p_process_name=>'Sauvegarder'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'',
unistr('   -- Liste des \00E9l\00E9ments associ\00E9s dans la base de donn\00E9es'),
'   CURSOR C_EVENEMENT_ASSOC IS',
'      SELECT EVC.NOM_EVENEMENT ',
'        FROM #OWNER#.EVENEMENT       EVE',
'            ,#OWNER#.EVENEMENT_CIBLE EVC',
'       WHERE EVE.TYPE_CIBLE      = ''BD''',
'         AND EVE.SOUS_TYPE_CIBLE = SUBSTR(:P312_NOM_CIBLE,1,2)',
'         AND EVE.TYPE_EVENEMENT  = ''MN''',
'         AND EVE.TYPE_CIBLE      = EVC.TYPE_CIBLE',
'         AND EVE.SOUS_TYPE_CIBLE = EVC.SOUS_TYPE_CIBLE',
'         AND EVE.NOM_EVENEMENT   = EVC.NOM_EVENEMENT',
'         AND EVC.NOM_CIBLE       = SUBSTR(:P312_NOM_CIBLE,4)',
'      ORDER BY NOM_EVENEMENT;',
'',
'   V_TABLEAU_BD  HTMLDB_APPLICATION_GLOBAL.VC_ARR2;',
'   V_TABLEAU_EC  HTMLDB_APPLICATION_GLOBAL.VC_ARR2;',
'   V_INDICE      NUMBER;',
'   V_EXISTE      BOOLEAN;',
'',
'   --',
unistr('   -- Proc\00E9dure locale de mise \00E0 jour des \00E9v\00E9nements de collectes de donn\00E9es'),
'   --',
'   PROCEDURE MAJ_CD',
'   (',
'      A_VALEUR_ELEMENT VARCHAR2',
'     ,A_NOM_EVENEMENT  VARCHAR2',
'   )',
'   IS',
'',
'   BEGIN',
'',
'      IF (A_VALEUR_ELEMENT = ''AC'') THEN',
'',
'         INSERT INTO #OWNER#.EVENEMENT_CIBLE',
'         (',
'            TYPE_CIBLE',
'           ,SOUS_TYPE_CIBLE',
'           ,NOM_CIBLE',
'           ,NOM_EVENEMENT',
'           ,VERIFICATION',
'           ,DH_PROCHAINE_VERIF',
'         )',
'         VALUES',
'         (',
'            ''BD''',
'           ,SUBSTR(:P312_NOM_CIBLE,1,2)',
'           ,SUBSTR(:P312_NOM_CIBLE,4)',
'           ,A_NOM_EVENEMENT',
'           ,''AC''',
'           ,SYSDATE',
'         );',
'',
unistr('         -- V\00E9rification si l''ev\00E9nement est nouveau (sinon, enregistrement quotidien en double)'),
'         IF (A_NOM_EVENEMENT = ''CD_DBA_DATA_FILES'') THEN',
'',
'            DELETE FROM #OWNER#.CD_DBA_DATA_FILES',
'               WHERE DH_COLLECTE_DONNEE >= TRUNC(SYSDATE)',
'                 AND NOM_CIBLE           = SUBSTR(:P312_NOM_CIBLE,4);',
'',
'         END IF;',
'',
'      ELSE',
'',
'         DELETE FROM #OWNER#.EVENEMENT_CIBLE',
'          WHERE TYPE_CIBLE      = ''BD''',
'            AND SOUS_TYPE_CIBLE = SUBSTR(:P312_NOM_CIBLE,1,2)',
'            AND NOM_CIBLE       = SUBSTR(:P312_NOM_CIBLE,4)',
'            AND NOM_EVENEMENT   = A_NOM_EVENEMENT;',
'',
'      END IF;',
'',
'   EXCEPTION',
'',
'      WHEN DUP_VAL_ON_INDEX THEN',
'         NULL;',
'',
'   END MAJ_CD;',
'',
'BEGIN',
'',
'   --',
'   -- Gestion des alertes',
'   --',
'   IF (:P312_ALERT = ''AC'') THEN',
'',
'      UPDATE #OWNER#.EVENEMENT_CIBLE',
'         SET DESTI_NOTIF = DECODE(:P312_SURCHARGE_NOTIF_ALERT',
'                                 ,''!!NULL!!'',TO_CHAR(NULL)',
'                                 ,:P312_SURCHARGE_NOTIF_ALERT',
'                                 )',
'       WHERE TYPE_CIBLE      = ''BD''',
'         AND SOUS_TYPE_CIBLE = SUBSTR(:P312_NOM_CIBLE,1,2)',
'         AND NOM_CIBLE       = SUBSTR(:P312_NOM_CIBLE,4)',
'         AND NOM_EVENEMENT   = ''ALERT'';',
'',
'      IF (SQL%ROWCOUNT = 0) THEN',
'',
'         INSERT INTO #OWNER#.EVENEMENT_CIBLE',
'         (',
'            TYPE_CIBLE',
'           ,SOUS_TYPE_CIBLE',
'           ,NOM_CIBLE',
'           ,NOM_EVENEMENT',
'           ,VERIFICATION',
'           ,DH_PROCHAINE_VERIF',
'           ,DESTI_NOTIF',
'         )',
'         VALUES',
'         (',
'            ''BD''',
'           ,SUBSTR(:P312_NOM_CIBLE,1,2)',
'           ,SUBSTR(:P312_NOM_CIBLE,4)',
'           ,''ALERT''',
'           ,''AC''',
'           ,SYSDATE',
'           ,DECODE(:P312_SURCHARGE_NOTIF_ALERT',
'                  ,''!!NULL!!'',TO_CHAR(NULL)',
'                  ,:P312_SURCHARGE_NOTIF_ALERT',
'                  )',
'         );',
'',
'      END IF;',
'',
'   ELSE',
'',
'      DELETE FROM #OWNER#.EVENEMENT_CIBLE',
'       WHERE TYPE_CIBLE      = ''BD''',
'         AND SOUS_TYPE_CIBLE = SUBSTR(:P312_NOM_CIBLE,1,2)',
'         AND NOM_CIBLE       = SUBSTR(:P312_NOM_CIBLE,4)',
'         AND NOM_EVENEMENT   = ''ALERT'';',
'',
'   END IF;',
'',
'',
'   --',
unistr('   -- Gestion des collectes de donn\00E9es'),
'   --',
'',
unistr('   -- V\00E9rification si la collecte de donn\00E9es doit \00EAtre trait\00E9e (Oracle / MSSQL)'),
'   SELECT COUNT(1)',
'     INTO V_INDICE',
'     FROM DUAL',
'     WHERE ((SUBSTR(:P312_NOM_CIBLE,1,2) = ''OR'') OR (SUBSTR(:P312_NOM_CIBLE,1,2) = ''MS''))',
'       AND EXISTS (SELECT 1',
'                       FROM #OWNER#.CIBLE',
'                      WHERE TYPE_CIBLE = ''BD''',
'                        AND NOM_CIBLE  = SUBSTR(:P312_NOM_CIBLE,4)',
'                 );',
'',
'   IF (V_INDICE != 0) THEN',
'',
'      IF (SUBSTR(:P312_NOM_CIBLE,1,2) = ''OR'') THEN',
'         MAJ_CD(:P312_CD_ESPACE_ARCHIVED_LOG, ''CD_ESPACE_ARCHIVED_LOG'');',
'      END IF;',
'',
'      IF (SUBSTR(:P312_NOM_CIBLE,1,2) = ''MS'') THEN',
'         MAJ_CD(:P312_CD_TRANSACTION_LOG, ''CD_TRANSACTION_LOG'');',
'      END IF;',
'',
unistr('      -- Gestion de la d\00E9pendance '),
'      IF (:P312_CD_FILESTAT = ''AC'') THEN',
'         MAJ_CD(''AC'', ''CD_DBA_DATA_FILES'');',
'      ELSE',
'         MAJ_CD(:P312_CD_DBA_DATA_FILES, ''CD_DBA_DATA_FILES'');',
'      END IF;',
'',
'      IF (SUBSTR(:P312_NOM_CIBLE,1,2) = ''OR'') THEN',
'         MAJ_CD(:P312_CD_FILESTAT, ''CD_FILESTAT'');',
'         MAJ_CD(:P312_CD_SYSSTAT_CPU, ''CD_SYSSTAT_CPU'');',
'         MAJ_CD(:P312_CD_ASM_DISKGROUP,''CD_ASM_DISKGROUP'');',
'      END IF;',
'',
'   END IF;',
'',
'',
'   --',
'   -- Gestion des associations',
'   --',
'',
unistr('   -- Obtenir la liste des \00E9l\00E9ments actuellement associ\00E9s BD'),
'   V_INDICE := 1;',
'   FOR RC_EVENEMENT_ASSOC IN C_EVENEMENT_ASSOC LOOP',
'      V_TABLEAU_BD(V_INDICE) := RC_EVENEMENT_ASSOC.NOM_EVENEMENT;',
'      V_INDICE := V_INDICE + 1;',
'   END LOOP;',
'',
unistr('   -- Obtenir la liste des \00E9l\00E9ments actuellement associ\00E9s \00C9CRAN'),
'   V_TABLEAU_EC := APEX_UTIL.STRING_TO_TABLE(:P312_ASSOCIATION_EVENEMENT);',
'',
'   --',
unistr('   -- Parcours de la liste pour g\00E9rer les retraits'),
'   --',
'',
unistr('   -- Pour chaque \00E9l\00E9ment de la liste BD'),
'   FOR I IN 1..V_TABLEAU_BD.COUNT LOOP',
'',
'      -- Recherche dans la liste active (si n''existe pas, retrait)',
'      V_EXISTE := FALSE;',
'      FOR J IN 1..V_TABLEAU_EC.COUNT LOOP',
'',
'         -- Test de l''existance',
'         IF (V_TABLEAU_BD(I) = V_TABLEAU_EC(J)) THEN',
'            V_EXISTE := TRUE;',
'            EXIT;',
'         END IF;',
'',
'      END LOOP;',
'',
unistr('      -- Si l''\00E9l\00E9ment n''a pas \00E9t\00E9 localis\00E9'),
'      IF (V_EXISTE = FALSE) THEN',
'',
'         DELETE FROM #OWNER#.EVENEMENT_CIBLE',
'          WHERE TYPE_CIBLE      = ''BD''',
'            AND SOUS_TYPE_CIBLE = SUBSTR(:P312_NOM_CIBLE,1,2)',
'            AND NOM_CIBLE       = SUBSTR(:P312_NOM_CIBLE,4)',
'            AND NOM_EVENEMENT   = V_TABLEAU_BD(I);',
'',
'      END IF;',
'',
'   END LOOP;',
'',
'   --',
unistr('   -- Parcours de la liste pour g\00E9rer les ajouts'),
'   --',
'',
unistr('   -- Pour chaque \00E9l\00E9ment de la liste active'),
'   FOR I IN 1..V_TABLEAU_EC.COUNT LOOP',
'',
'      -- Recherche dans la liste BD (si n''existe pas, ajout)',
'      V_EXISTE := FALSE;',
'      FOR J IN 1..V_TABLEAU_BD.COUNT LOOP',
'',
'         -- Test de l''existance',
'         IF (V_TABLEAU_EC(I) = V_TABLEAU_BD(J)) THEN',
'            V_EXISTE := TRUE;',
'            EXIT;',
'         END IF;',
'',
'      END LOOP;',
'',
unistr('      -- Si l''\00E9l\00E9ment n''a pas \00E9t\00E9 localis\00E9'),
'      IF (V_EXISTE = FALSE) THEN',
'',
'         INSERT INTO #OWNER#.EVENEMENT_CIBLE',
'         (',
'            TYPE_CIBLE                     ',
'           ,SOUS_TYPE_CIBLE                ',
'           ,NOM_CIBLE                      ',
'           ,NOM_EVENEMENT                  ',
'           ,VERIFICATION                   ',
'           ,DH_PROCHAINE_VERIF             ',
'         )',
'         VALUES',
'         (',
'            ''BD''',
'            ,SUBSTR(:P312_NOM_CIBLE,1,2)',
'            ,SUBSTR(:P312_NOM_CIBLE,4)',
'            ,V_TABLEAU_EC(I)',
'            ,:P312_TYPE_SAUVEGARDE',
'            ,SYSDATE',
'         );',
'',
'      END IF;',
'',
'   END LOOP;',
'',
'END;',
'',
'#OWNER#.SDBM_APEX_UTIL.AJUSTER_EVENEMENT_REF_TYPE(SUBSTR(:P312_NOM_CIBLE,4));'))
